import React from 'react';

class Desc extends React.Component{

    render() {
        const textarea =  this.state;
        const { textarChange } = this.props
        return (
            <textarea value={textarea}
            onChange={this.props.textarChange}></textarea>
        )
    }
}

export default Desc;
